const type = {
    base: "EncodeSansRegular",
    baseBold: "EncodeSansBold",
    bold: "MontserratBold",
    boldRegular: "MontserratRegular",
};

const size = {
    small: 10,
    caption: 12,
    body: 14,
    subheader: 16,
    title: 18,
    headline: 20,
    display: 22,
    jumbo: 26,
    mega: 32,
};

export default {
    type,
    size,
};
