import React, { useState } from 'react';
import { Text, View, Image, StyleSheet, Alert, ActivityIndicator } from 'react-native';
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler';
import Button from '../components/Button';
import { firebase } from '../firebase/config'
const styles = StyleSheet.create({
    form: {
        marginHorizontal: 25,
        marginTop: 60,
    },
    input: {
        height: 40,
        borderBottomColor: '#ddd',
        borderBottomWidth: 1,
        marginBottom: 30,
        padding: 10
    },
    signUpView: {
        flex: 1,
        justifyContent: "flex-end", alignItems: "center"
    }
})

const Signup = ({ navigation }) => {
    const [email, setEmail] = useState(null);
    const [password, setPassword] = useState(null);
    const [loading, setLoading] = useState(false);

    const login = () => {
        if (!email || !password) {
            return Alert.alert("Error", "You need to fill all the inputs", [{ text: "OK", onPress: () => { } },

            ]);
        }
        setLoading(true);
        firebase.auth()
            .signInWithEmailAndPassword(email, password)
            .then(res => {
                console.log(res);
                setLoading(false);
                navigation.navigate('Home');
            })
            .catch(err => {
                console.log(err);
                alert("Sign In Failed");
                setLoading(false);
            })
    }
    return (
        <View style={{ flex: 1, backgroundColor: "white" }}>
            <View style={{ alignSelf: 'center', marginTop: 50 }}>
                />
            </View>
            <View style={styles.form}>
                <TextInput onChangeText={(text) => setEmail(text)} placeholder="Email" style={styles.input} />
                <TextInput onChangeText={(text) => setPassword(text)} placeholder="Password" secureTextEntry={true} style={styles.input} />
                {loading ? <ActivityIndicator /> : <Button onPress={login} title="Login" backgroundColor="blue" />}
            </View>

            <View style={styles.signUpView}>
                <TouchableOpacity style={{ padding: 20 }} onPress={() => navigation.navigate("Signup")}>
                    <Text style={{ fontSize: 16, fontWeight: "bold" }}>Don't have an account? <Text style={{ color: "blue" }}>Sign up</Text></Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

export default Signup;
