import React from 'react'
import { StyleSheet, View, Image } from 'react-native'
import { Colors, Images, Metrics } from '../../theme'
import cs from '../../theme/common-styles'
import { Formik } from 'formik'
import * as yup from 'yup';
import { TextInput } from 'react-native-gesture-handler'

const validationSchema = yup.object().shape({
    email: yup.string().trim().label('Email').email().required('Email field is empty'),
    password: yup.string().label('Password').email().required('Password field is empty')
})
export default function Login() {
    return (
        <View style={cs.container}>
            <Image source={Images.landing} style={{ width: '100%' }} />
            <Formik initialValues={{ email: '', password: '' }}
                onSubmit={() => { }}
                validationSchema={validationSchema}>
                <View style={styles.formWrapper}>
                    <TextInput
                        placeholder="Email"
                        formikProps={formikProps}
                        formikKey={"email"}
                        autoCapitalize="none" />
                </View>
            </Formik>
        </View>
    )
}

const styles = StyleSheet.create({
    formWrapper: {
        marginTop: Metrics.doubleBase,
        marginHorizontal: Metrics.base
    }
})
